","keccak256":"0x5f7e4076e175393767754387c962926577f1660dd9b810187b9002407656be72","license":"MIT"},"contracts/BuildingDAO.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "./VoteToken.sol";
import "./Vote.sol";

contract BuildingDAO {
    struct Building {
        string buildingName;
        address treasury;
        uint256 totalApartments;
    }

    struct Owner {
        bool isActive;
        address delegateTo;
    }

    enum ProposalType {
        MINOR_CHANGE,
        MODERATE_CHANGE,
        MAJOR_CHANGE
    }

    Building public buildingInfo;
    address public admin;
    VoteToken public voteToken;
    mapping(address => Owner) public owners;
    mapping(uint256 => address) public proposalVotes; // proposalId => Vote contract
    uint256 public proposalCount;
    uint256 public constant VOTING_DELAY = 1 days;
    uint256 public constant VOTING_PERIOD = 7 days;

    event ProposalCreated(uint256 indexed proposalId, address voteContract);
    event OwnerAdded(address indexed owner, uint256 tokenId);
    event OwnerRemoved(address indexed owner, uint256 tokenId);
    event ProposalTiming(uint256 start, uint256 end, uint256 duration);

    modifier onlyAdmin() {
        require(msg.sender == admin, "Solo admin");
        _;
    }

    modifier onlyOwner() {
        require(owners[msg.sender].isActive, "Solo propietarios");
        _;
    }

    constructor(
        string memory _buildingName,
        uint256 _totalApartments,
        address[] memory _initialOwners,
        address _treasury
    ) {
        admin = msg.sender;
        buildingInfo.buildingName = _buildingName;
        buildingInfo.totalApartments = _totalApartments;
        buildingInfo.treasury = _treasury;

        // Deploy VoteToken
        voteToken = new VoteToken();

        // Mint tokens for initial owners
        for(uint i = 0; i < _initialOwners.length; i++) {
            require(_initialOwners[i] != address(0), "Direccion invalida");
            owners[_initialOwners[i]].isActive = true;
            voteToken.mint(_initialOwners[i], i + 1); // tokenId starts from 1
            emit OwnerAdded(_initialOwners[i], i + 1);
        }
    }

 function createProposal(
    string memory _title,
    string memory _description,
    ProposalType _type,
    bytes memory /* _callData */
) public onlyOwner returns (uint256) {
    // Basic validations
    require(bytes(_title).length > 0, "Title cannot be empty");
    require(bytes(_description).length > 0, "Description cannot be empty");
    require(voteToken.balanceOf(msg.sender) > 0, "No tokens owned");
    
    // Time validations
    uint256 startTime = block.timestamp;
    uint256 endTime = startTime + VOTING_PERIOD;
    emit ProposalTiming(startTime, endTime, VOTING_PERIOD);
    require(startTime < endTime, "Invalid voting period");
    require(endTime > block.timestamp, "End time must be in future");

    // Calculate quorum and majority
    uint256 quorum;
    uint256 requiredMajority;

    if (_type == ProposalType.MINOR_CHANGE) {
        quorum = (buildingInfo.totalApartments * 25) / 100;
        requiredMajority = 50;
    } else if (_type == ProposalType.MODERATE_CHANGE) {
        quorum = (buildingInfo.totalApartments * 50) / 100;
        requiredMajority = 60;
    } else if (_type == ProposalType.MAJOR_CHANGE) {
        quorum = (buildingInfo.totalApartments * 75) / 100;
        requiredMajority = 75;
    } else {
        revert("Invalid proposal type");
    }

    // Create new vote contract with try/catch
    try new Vote(
        _title,
        _description,
        startTime,
        endTime,
        quorum,
        requiredMajority,
        _type,
        msg.sender,
        address(this)
    ) returns (Vote newVote) {
        uint256 proposalId = proposalCount++;
        proposalVotes[proposalId] = address(newVote);
        
        emit ProposalCreated(proposalId, address(newVote));
        return proposalId;
    } catch Error(string memory reason) {
        revert(string(abi.encodePacked("Vote creation failed: ", reason)));
    } catch {
        revert("Vote creation failed");
    }
}
// BuildingDAO.sol - Add these functions

// Get Vote contract address by proposalId
function getVoteContract(uint256 _proposalId) public view returns (address) {
    return proposalVotes[_proposalId];
}

// Get number of proposals
function getProposalCount() public view returns (uint256) {
    return proposalCount;
}

// Get all active proposals
function getActiveProposals() public view returns (address[] memory) {
    address[] memory activeVotes = new address[](proposalCount);
    uint256 activeCount = 0;
    
    for(uint256 i = 0; i < proposalCount; i++) {
        address voteAddress = proposalVotes[i];
        if(voteAddress != address(0)) {
            Vote vote = Vote(voteAddress);
            if(!vote.executed() && block.timestamp <= vote.endTime()) {
                activeVotes[activeCount] = voteAddress;
                activeCount++;
            }
        }
    }
    
    // Resize array to actual count
    assembly {
        mstore(activeVotes, activeCount)
    }
    
    return activeVotes;
}

// Get proposal details
function getProposalDetails(uint256 _proposalId) public view returns (
    address voteContract,
    string memory title,
    string memory description,
    uint256 startTime,
    uint256 endTime,
    uint256 yesVotes,
    uint256 noVotes,
    bool executed
) {
    address voteAddress = proposalVotes[_proposalId];
    require(voteAddress != address(0), "Proposal does not exist");
    
    Vote vote = Vote(voteAddress);
    return (
        voteAddress,
        vote.title(),
        vote.description(),
        vote.startTime(),
        vote.endTime(),
        vote.yesVotes(),
        vote.noVotes(),
        vote.executed()
    );
}
    function getOwnerTokens(address _owner) external view returns (uint256) {
        return voteToken.balanceOf(_owner);
    }

    function addOwner(address _owner, uint256 _tokenId) public onlyAdmin {
        require(!owners[_owner].isActive, "Ya es propietario");
        owners[_owner].isActive = true;
        voteToken.mint(_owner, _tokenId);
        emit OwnerAdded(_owner, _tokenId);
    }

    function removeOwner(address _owner) public onlyAdmin {
        require(owners[_owner].isActive, "No es propietario");
        uint256 tokenId = voteToken.tokenOfOwnerByIndex(_owner, 0);
        voteToken.burn(tokenId);
        delete owners[_owner];
        emit OwnerRemoved(_owner, tokenId);
    }

    function delegate(address _to) public onlyOwner {
        require(_to != msg.sender, "No auto-delegacion");
        require(owners[_to].isActive || _to == address(0), "Delegado invalido");
        owners[msg.sender].delegateTo = _to;
    }

    function getVotingPower(address _owner) public view returns (uint256) {
        uint256 ownPower = voteToken.balanceOf(_owner);
        
        // Add delegated power
        for (address delegator = msg.sender; delegator != address(0);) {
            if (owners[delegator].delegateTo == _owner) {
                ownPower += voteToken.balanceOf(delegator);
            }
            delegator = owners[delegator].delegateTo;
        }
        
        return ownPower;
    }

    function sendVote(bool inFavor, address _proposalAddress) external {
    // Verifica que la dirección de la propuesta no sea la dirección cero
    require(_proposalAddress != address(0), "Proposal does not exist");

    // Llama a la función 'vote' del contrato Vote correspondiente
    Vote(_proposalAddress).vote(inFavor);
    }

}","keccak256":"0xeebc59db9b0e183ea1fee9f3077fb3ec21e6868507093bf7346b7cab65ed1e08","license":"MIT"},"contracts/Vote.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "./BuildingDAO.sol";

contract Vote {
    address public parentDao;
    BuildingDAO.ProposalType public proposal;

    string public title;
    string public description;

    uint256 public startTime;
    uint256 public endTime;
    uint256 public quorum;
    uint256 public requiredMajority; // Fixed spelling
    uint256 public yesVotes;
    uint256 public noVotes;
    address public creator;

    bool public executed;

    mapping(address => bool) public hasVoted;

    event Voted(address indexed voter, bool inFavor);
    event ProposalExecuted();
    event Debug(string message, uint256 value); // Add debug event

    constructor(
        string memory _title,
        string memory _description,
        uint256 _startTime,
        uint256 _endTime,
        uint256 _quorum,
        uint256 _requiredMajority,
        BuildingDAO.ProposalType _proposal,
        address _creator,
        address _parentDao
    ) {
        title = _title;
        description = _description;
        startTime = _startTime;
        endTime = _endTime;
        quorum = _quorum;
        requiredMajority = _requiredMajority;
        proposal = _proposal;
        creator = _creator;
        parentDao = _parentDao; // Fixed casing
    }

    function vote(bool inFavor) external {
        // Add debug events
        emit Debug("Current time", block.timestamp);
        emit Debug("Start time", startTime);
        emit Debug("End time", endTime);

        // Check if voting period is valid
        require(startTime < endTime, "Invalid voting period");

        // Check if voting is active
        require(block.timestamp >= startTime, "Voting not started");
        require(block.timestamp <= endTime, "Voting ended");
        require(!hasVoted[msg.sender], "Already voted");

        uint256 tokensOwned = BuildingDAO(parentDao).getOwnerTokens(msg.sender);
        require(tokensOwned > 0, "No tokens owned");

        hasVoted[msg.sender] = true;
        if (inFavor) {
            yesVotes += tokensOwned;
        } else {
            noVotes += tokensOwned;
        }

        emit Voted(msg.sender, inFavor);
    }

    function hasAddressVoted(address _voter) external view returns (bool) {
        return hasVoted[_voter];
    }

    function executeProposal() external {
        require(block.timestamp > endTime, "Voting ongoing");
        require(!executed, "Already executed");

        uint256 totalVotes = yesVotes + noVotes;
        require(totalVotes >= quorum, "Quorum not reached");

        if ((yesVotes * 100) / totalVotes >= requiredMajority) {
            executed = true;
            emit ProposalExecuted();
        }
    }

    function getVoteStatistics() external view returns (
        uint256 _yesVotes,
        uint256 _noVotes,
        uint256 _quorum,
        uint256 _requiredMajority,
        bool _hasEnded,
        bool _quorumReached,
        bool _majorityReached
    ) {
        uint256 totalVotes = yesVotes + noVotes;
        uint256 yesPercentage = totalVotes > 0 ? (yesVotes * 100) / totalVotes : 0;
        
        return (
            yesVotes,
            noVotes,
            quorum,
            requiredMajority,
            block.timestamp > endTime,
            totalVotes >= quorum,
            yesPercentage >= requiredMajority
        );
    }

    // Add helper function to check voting status
    function getVotingStatus() public view returns (
        uint256 currentTime,
        uint256 votingStart,
        uint256 votingEnd,
        bool isActive
    ) {
        return (
            block.timestamp,
            startTime,
            endTime,
            (block.timestamp >= startTime && block.timestamp <= endTime)
        );
    }
